#pragma once

#include "apps/mozart/lib/scene/client/resources.h"
#include "apps/mozart/lib/scene/client/session.h"

namespace shadertoy_client {

// Simple scene consisting of 10 rounded rectangles, each of which share a
// material whose texture is generated by the Shadertoy service.
class ExampleScene {
 public:
  ExampleScene(mozart::client::Session* session,
               const mozart::client::Material& material,
               float width,
               float height);

  void Update(float animation_time);

 private:
  float width_;
  float height_;
  mozart::client::DisplayRenderer renderer_;
  mozart::client::RoundedRectangle shape_;
  mozart::client::ShapeNode background_;
  mozart::client::ShapeNode rect0_;
  mozart::client::ShapeNode rect1_;
  mozart::client::ShapeNode rect2_;
  mozart::client::ShapeNode rect3_;
  mozart::client::ShapeNode rect4_;
  mozart::client::ShapeNode rect5_;
  mozart::client::ShapeNode rect6_;
  mozart::client::ShapeNode rect7_;
  mozart::client::ShapeNode rect8_;
  mozart::client::ShapeNode rect9_;
};

}  // namespace shadertoy_client
