#define WMI_SERVICE_IS_ENABLED(wmi_svc_bmap, svc_id, len) \
    ((svc_id) < (len) && \
     (wmi_svc_bmap)[(svc_id) / (sizeof(uint32_t))] & \
     (1 << ((svc_id) % (sizeof(uint32_t)))))

#define SVCMAP(x, y, len) \
    do { \
